from pybricks.hubs import TechnicHub
from pybricks.iodevices import XboxController
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor

# Set up all devices.
technic_hub = TechnicHub()
steer1 = Motor(Port.A, Direction.CLOCKWISE)
steer2 = Motor(Port.B, Direction.CLOCKWISE)
drive1 = Motor(Port.C, Direction.CLOCKWISE)
drive2 = Motor(Port.D, Direction.CLOCKWISE)
xbox = XboxController()


# The main program starts here.
while True:
    if xbox.triggers()[1]:
        drive1.run(25 * xbox.triggers()[1])
        drive2.run(25 * xbox.triggers()[1])
    else:
        if xbox.triggers()[0]:
            drive1.run(-25 * xbox.triggers()[0])
            drive2.run(-25 * xbox.triggers()[0])
        else:
            drive1.brake()
            drive2.brake()
    if xbox.joystick_left()[0]:
        steer1.run(25 * xbox.joystick_left()[0])
        steer2.run(25 * xbox.joystick_left()[0])
    else:
        steer1.brake()
        steer2.brake()
